package com.github.zly2006.xbackup.mc120.mixin;

import net.minecraft.client.gui.screen.world.SelectWorldScreen;
import net.minecraft.client.gui.widget.ButtonWidget;
import net.minecraft.text.Text;
import org.spongepowered.asm.mixin.Mixin;
import org.spongepowered.asm.mixin.injection.At;
import org.spongepowered.asm.mixin.injection.Inject;
import org.spongepowered.asm.mixin.injection.callback.CallbackInfo;
import net.minecraft.client.gui.screen.Screen;

@Mixin(SelectWorldScreen.class)
public class MixinSelectWorldScreen extends Screen {
    protected MixinSelectWorldScreen(Text title) {
        super(title);
    }

    @Inject(at = @At("RETURN"), method = "init()V")
    private void init(CallbackInfo ci) {
        addDrawableChild(ButtonWidget.builder(Text.of("R"), button -&gt; System.out.println("test ok")).dimensions(this.width / 2 + 20, this.height - 52, 150, 20).build());
    }
}

